import axios from "axios";

import { ORDER_CREATE_REQUEST, ORDER_CREATE_SUCCESS, ORDER_CREATE_FAIL, ORDER_DETAILS_REQUEST, ORDER_DETAILS_SUCCESS, ORDER_DETAILS_FAIL, ORDER_PAY_REQUEST} from '../constants/orderConstant';

export const createdOrder = (order) => async (dispatch, getState) => {

    try {
        dispatch({
            type: ORDER_CREATE_REQUEST
        })

        const {userLogin : {userInfo}, } = getState()

        const config = {
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${userInfo.token}`,
            },
          }

        const { data } = await axios.post(`/api/orders`, order, config)
        console.log(data);
        dispatch({
            type: ORDER_CREATE_SUCCESS,
            payload : data
        })

    } catch (error) {
        dispatch({
            type : ORDER_CREATE_FAIL,
            payload : error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
        })
    }
}

export const getOrderDetails = (id) => async (dispatch, getState) => {
   try {
     dispatch({type: ORDER_DETAILS_REQUEST})

     const {userLogin : {userInfo }, } = getState()

     const config = {
        headers : {
            Authorization : `Bearer ${userInfo.token}`
        }
     }

     const { data } = await axios.get(`/api/orders/${id}`, config);

     dispatch({
        type : ORDER_DETAILS_SUCCESS,
        payload : data
     })

   } catch (error) {
      dispatch({
        type : ORDER_DETAILS_FAIL,
        payload : error.response && error.response.data.message
        ? error.response.data.message
        : error.message,
      })
   }
}

const payOrder = (orderId, paymentResult) => async (dispatch, getState) => {
  try {

    dispatch({
        type: ORDER_PAY_REQUEST
    });

    const {userLogin : {userInfo}, } = getState() 

    const config = {
        headers : {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${userInfo.token}`,
        }
    }
  } catch (error) {

  }
}
