import axios from "axios";

import { ORDER_CREATE_REQUEST, ORDER_CREATE_SUCCESS, ORDER_CREATE_FAIL} from '../constants/orderConstant';

export const createdOrder = (order) => async (dispatch, getState) => {

    try {
        dispatch({
            type: ORDER_CREATE_REQUEST
        });

        const {userLogin : {userInfo}, } = getState()

        const config = {
            headers : {
                'Content-Type' : 'appilication/json',
                Authorization : `Bearer ${userInfo.token}`
            }
        }
        console.log('order', order);
        const { data } = await axios.post('/api/orders', config, order)
        console.log(data);
        dispatch({
            type: ORDER_CREATE_SUCCESS,
            payload : data
        })

    } catch (error) {
        dispatch({
            type : ORDER_CREATE_FAIL,
            payload : error.response && error.response.data.message
            ? error.response.data.message
            : error.message,
        })
    }
}
