import React, {useEffect, useState} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {Form, Col, Row, Button} from 'react-bootstrap'
import { Link, useNavigate, useLocation } from 'react-router-dom'
import Loader from '../Components/Loader'
import Messages from '../Components/Messages'
import { register, getUserDetails } from '../actions/userAction'


const ProfileScreen = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('')

    const dispatch = useDispatch();
    const location = useLocation();
    const navigate = useNavigate();

    const  userLogin  = useSelector((state) => state.userLogin)
    const { userInfo } = userLogin

    const userDetails = useSelector((state) => state.userDetails);
    const {loading, error, user} = userDetails;
    useEffect(() => {
        if(!userInfo){
            navigate('/login')
        }else{
         if(!user){
            dispatch(getUserDetails('profile'))
         } else {
            setName(user.name);
            setEmail(user.email)
         }
        }
    }, [userInfo, navigate, redirect, user])

    const UpdateHandler = (e) => {
        e.preventDefault();
        if(password !== confirmPassword){
           setMessage('Password do not match')
        }else{
            //Dispatch
        }

    }

  return (
    <Row>
        <Col md={3}>
        <h2>Profile</h2>
        {message && <Messages variant='danger'>{message}</Messages>}
        {error && <Messages variant='danger'>{error}</Messages>}
        {loading && <Loader />}

        <Form onSubmit={UpdateHandler}>
            <Form.Group controlId='name'>
                <Form.Label>Enter Name</Form.Label>
                <Form.Control 
                type='text' 
                placeholder='Enter Name' 
                value={name}
                onChange={(e) => setName(e.target.value)}
                >
                </Form.Control>
            </Form.Group>
            <Form.Group controlId='email'>
                <Form.Label>Enter Email</Form.Label>
                <Form.Control
                type='email'
                placeholder='Enter email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                >
                </Form.Control>
            </Form.Group>
            <Form.Group controlId='password'>
                <Form.Label>Enter Password</Form.Label>
                <Form.Control
                type='password'
                placeholder='Enter password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                >
                </Form.Control>
            </Form.Group>
            <Form.Group controlId='confirmPassword'>
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                type='password'
                placeholder='Enter Confirm Password'
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                >
                </Form.Control>
            </Form.Group>
            <Button style={{marginTop: '20px'}} type='submit' variant='primary'>Sign Up</Button>
        </Form>
        </Col>
         <Col md={9}>

         </Col>  
    </Row>
  )
}

export default ProfileScreen
